mapper.select:
SELECT l.cont_no AS contractNo, l.approve_amt AS approveAmt, l.approve_term AS approveTerm, l.product_id as productId, l.id as id, l.approve_time as approveTime, l.status as status FROM loan_apply l WHERE
l.user_id = #{id}
AND l.del = 0 ORDER BY l.create_time DESC

SELECT c.id_no as idNo, c.mobile as mobile, c.qq as qq, c.bank_code as bankCode, c.card_no as cardNo, c.real_name as realName, c.email as email, ci.reside_province as resideProvince, ci.reside_city as resideCity, ci.reside_district as resideDistrict, ci.reside_addr as resideAddr, l.serv_fee_rate as servFeeRate, l.basic_rate as basicRate, l.purpose as purpose, l.cont_no as contractNo, l.approve_amt as principal, l.serv_fee as servFee, l.term as totalTerm, l.approve_term as approveTerm, l.repay_unit as repayUnit, l.actual_rate as actualRate, l.repay_method as repayMethod FROM loan_apply l LEFT JOIN cust_user c ON l.user_id = c.id LEFT JOIN cust_user_info ci ON l.user_id = ci.id WHERE
l.id = #{contNo}
AND l.del = 0

